import styled from "styled-components";

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    background: 
     linear-gradient(rgba(255,255,255,0.3), rgba(255,255,255,0.3)),
     url("https://www.investigo.co.uk/rails/active_storage/blobs/eyJfcmFpbHMiOnsibWVzc2FnZSI6IkJBaHBBNk40Qmc9PSIsImV4cCI6bnVsbCwicHVyIjoiYmxvYl9pZCJ9fQ==--9b8088b8ca7747465c2b046df4ddef04836f0f8e/shutterstock_746441521.jpg")
     center;
     background-size: cover
     ;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const Wrapper = styled.div`
    width: 40%;
    padding: 1.25rem;
    background-color: white;

`;

const Title = styled.h1`
    font-size: 1.25rem;
    font-weight: 300;
`;

const Form = styled.form`
    display: flex;
    flex-wrap: wrap;
`;

const Input = styled.input`
    flex: 1;
    min-width: 40%;
    margin: 1.25rem 0.625rem 0 0;
    padding: 0.625rem;
    border:none;
    border-bottom: 1px solid lightgray;
    outline: none
`;

const Agreement = styled.span`
    font-size: 0.75rem;
    margin: 1.25rem 0;
`;

const Button = styled.button`
     width: 40%;
     background-color: teal;
     color: white;
     padding: 15px 20px;
     cursor: pointer;
     border: none;
     margin-bottom: 10px;
     &:disabled{
       cursor: not-allowed;
       color: green;
     }
`;

const Link = styled.a`
  margin: 5px 0;  
  font-size: 12px;
  text-decoration: underline; 
  cursor: pointer;
`;

const Login = () => {
  return (
    <Container>
    <Wrapper>
        <Title>SIGN IN</Title>
        <Form>
            <Input
             placeholder="Username"
             name="username"

              />
            <Input
             placeholder="Password"
             type="password"

              />
            <Button >LOGIN</Button>

            <Link>DO NOT REMEMBER PASSWORD?</Link>
            <Link>CREATE A NEW ACCOUNT</Link>
        </Form>
  </Wrapper>
</Container>
  )
}

export default Login
